import math

class SimpleAlegbra():
    def __init__(self, a, b):
        self.a = a
        self.b = b
        
    def addition(a, b):
        return self.a + self.b
    
    def substraction(a,b):
        return self.a - self.b
    
    def multiplication(a,b):
        return self.a * self.b

        